// Alert
var SkatchAlertClose = document.querySelectorAll(".AlertClose");
if(SkatchAlertClose){
for(var i=0;i<SkatchAlertClose.length;i++){
SkatchAlertClose[i].addEventListener("click",function(e){
    this.parentElement.style.display="none";
});
}
}

// Header
var menuIcon = document.querySelector("#HeaderMenuicon");
var menuItems = document.querySelector(".menuItems");
if(menuIcon){
menuIcon.addEventListener("click",function(e){
    menuItems.classList.toggle('HeaderMenushow');
});
}

// Second Header
var leftHeaderMenu = document.querySelector("#LeftHeaderMenuicon");
var SlidindMenu = document.querySelector(".leftHeaderMenuOptions");
if(leftHeaderMenu){
leftHeaderMenu.addEventListener("click",function(e){
    SlidindMenu.classList.toggle("leftSlidingMenuShow");
});
}

// Popup
var openPopup = document.getElementsByClassName("open-popup-btn");
var closePopup = document.getElementsByClassName("dismiss-popup-btn");

if(closePopup){
for (var iter = 0; iter < closePopup.length; iter++) {
closePopup[iter].addEventListener("click",function(e){
    this.parentNode.parentNode.classList.remove("SkatchPopupactive");
});}}

if(openPopup){
for (var i = 0; i < closePopup.length; i++) {
openPopup[i].addEventListener("click",function(e){
  document.getElementById(this.getAttribute('popup-target')).classList.add("SkatchPopupactive");
});
} 
}

// Toast
var SkatchNotificationTriggre = document.getElementsByClassName("SkatchNotificationTriggre");
if(SkatchNotificationTriggre){
    for (var iter=0;iter < SkatchNotificationTriggre.length;iter++){
        SkatchNotificationTriggre[iter].addEventListener("click",function(e){
            var notification_target = this.getAttribute('notification-target');
            document.getElementById(notification_target).classList.add("SkatchNotificatonAppear");
            setTimeout(function(){
                document.getElementById(notification_target).classList.remove("SkatchNotificatonAppear");
            }, 5000);
    });
    }
}